import React from "react";
import { connect } from "react-redux";
import { confirmMatch } from "../actions";
import ImageUploader from "./ImageUploader";

class EditProfile extends React.Component {
  state = {
      // name: this.props.currentUser.info.name, 
      // summary: this.props.currentUser.info.name,
      image: null
  };

  clickHandler = (event) => {
    let formdata = new FormData();
    formdata.append("image", this.state.image)

    fetch(`http://localhost:3000/user/${this.props.currentUser.info.id}/upload_img`, {
        method: "POST",
        body: formdata
    })
  }

  selectImage = (image) => {
    this.setState({image})
  }

  render() {
    return !this.props.currentUser ? (
      <div>Loading ...</div>
    ) : (
      <div className="user-edit-form">
        <div className="user-banner">
          <div className="user-name-image">
            <Avatar
              style={{
                margin: "10px",
                width: "180px",
                height: "180px",
              }}
              src={this.props.currentUser.images[0]}
            />
            <h1>{this.props.currentUser.info.name}</h1>
            <p>New York, NY</p>
            <p>
              {this.props.currentUser.info.gender.toUpperCase()},{" "}
              {this.props.currentUser.info.age}
            </p>
            <Link to="/user_profile/edit">
              <button className="ui blue button">Edit Profile</button>
            </Link>
          </div>

          <div className="user-info">
            <div className="user-summary">
              <h1> Summary </h1>
              <p> {this.props.currentUser.info.summary} </p>
            </div>

            <div className="user-work-info">
              <h1> Work Info </h1>
              <p> {this.props.currentUser.info.work_info} </p>
            </div>
          </div>
        </div>
        <div className="user-add-image">
          <ImageUploader selectImage={this.selectImage} />
        </div>
        <button onClick={this.clickHandler}>Submit</button>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return { currentUser: state.auth.currentUser };
};

export default connect(mapStateToProps, { confirmMatch })(EditProfile);
