import React from "react";
import { connect } from "react-redux";
import TextField from "@material-ui/core/TextField";
import { InputLabel } from "@material-ui/core";
import ImageUploader from "./ImageUploader";

class EditProfile extends React.Component {
  state = {
    // name: this.props.currentUser.info.name,
    // summary: this.props.currentUser.info.name,
    image: null,
  };

  clickHandler = (event) => {
    let formdata = new FormData();
    formdata.append("image", this.state.image);

    fetch(
      `http://localhost:3000/user/${this.props.currentUser.info.id}/upload_img`,
      {
        method: "POST",
        body: formdata,
      }
    );
  };

  selectImage = (image) => {
    this.setState({ image });
  };

  render() {
    return !this.props.currentUser ? (
      <div>Loading ...</div>
    ) : (
      <div className="user-edit-form">
        <form>
          <ImageUploader selectImage={this.selectImage} />
          <div className="edit-user-info">

            <TextField
              id="outlined-name"
              label="Name"
              // value={name}
              // onChange={handleChange}
              variant="outlined"
            />

            <TextField
              id="outlined-work-info"
              label="Work Info"
              // value={name}
              // onChange={handleChange}
              variant="outlined"
              />
              </div>

            <TextField
              id="outlined-budget"
              label="Budget"
              type="number"
              InputLabelProps={{
                shrink: true,
              }}
              variant="outlined"
            />

          {/* <button onClick={this.clickHandler}>Submit</button> */}
        </form>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return { currentUser: state.auth.currentUser };
};

export default connect(mapStateToProps)(EditProfile);
