import React from 'react';
import UserCard from './UserCard'
import { fetchUsers } from '../actions'
import { connect } from 'react-redux';
import { setSelectedUser } from '../actions'

class FindRoomate extends React.Component {


    componentDidMount () {
        console.log(this.props.currentUser)
        this.props.fetchUsers(this.props.currentUser)
    }

    clickHandler = selectedUser => {
        this.props.setSelectedUser(selectedUser)
    }

    render () {
        return (
            // !this.props.currentUser ? <div>LOADING...</div> : (    
            <div className="ui grid container">
                {this.props.users.map(user => <UserCard onClick={this.clickHandler} key={user.id} {...user} />)}
            </div>

        ) 
    }

}

const mapStateToProps = state => {
    return {
        users: state.users.users,
        currentUser: state.auth.currentUser
    }
}

export default connect(mapStateToProps, { fetchUsers, setSelectedUser })(FindRoomate)
