import React from 'react';
import { connect } from 'react-redux'

class UserProfile extends React.Component {

    render() {
        console.log(this.props)
        return (
            // !this.props.currentUser ? <div>Loading ...</div>:
            <div className="user-profile">
                <div className="user-card">
                    <img src="https://cdn2.iconfinder.com/data/icons/avatar-profile/449/avatar_user_default_contact_profile_male-512.png" alt="user-profile"/>
                    <div className="uiuser-summary">
                    <h1>{this.props.currentUser.name}</h1>
                    <p>{this.props.currentUser.summary}</p>
                    </div>
                </div>


            </div>
        )
    }
}

const mapStateToProps = state => {
    return {currentUser: state.auth.currentUser}
}

export default connect(mapStateToProps)(UserProfile)
