import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import InputLabel from "@material-ui/core/InputLabel";
import MenuItem from "@material-ui/core/MenuItem";
import FormHelperText from "@material-ui/core/FormHelperText";
import FormControl from "@material-ui/core/FormControl";
import Select from "@material-ui/core/Select";
import Typography from "@material-ui/core/Typography";
import Slider from "@material-ui/core/Slider";

const useStyles = makeStyles((theme) => ({
  formControl: {
    margin: theme.spacing(1),
    minWidth: 120,
  },
  selectEmpty: {
    marginTop: theme.spacing(2),
  },
}));

function valueLabelFormat(value) {
  return `$ ${value}`;
}

const Filter = (props) => {
  const classes = useStyles();
  const [gender, setGender] = React.useState("");
  const [budget, setBudget] = React.useState(100);

  const handleGender = (event) => {
    setGender(event.target.value);
    props.handleFilter(event.target.name, event.target.value);
  };

  const handleBudget = (event) => {
    setBudget(event.target.value);
    props.handleFilter(event.target.name, event.target.value);
  };

  return (
    <div className="filter">
      <FormControl className={classes.formControl}>
        <InputLabel id="gender-input">Gender</InputLabel>
        <Select value={gender} onChange={handleGender} name="gender">
          <MenuItem value="">
            <em>None</em>
          </MenuItem>
          <MenuItem value={"male"}>Male</MenuItem>
          <MenuItem value={"female"}>Female</MenuItem>
        </Select>
        <FormHelperText>Filter By Gender</FormHelperText>
      </FormControl>

      <Typography id="linear-slider" gutterBottom>
        Filter By Budget
      </Typography>
      <Slider
        name="budget"
        value={budget}
        min={0}
        step={100}
        max={5000}
        getAriaValueText={valueLabelFormat}
        valueLabelFormat={valueLabelFormat}
        onChange={handleBudget}
        valueLabelDisplay="auto"
        aria-labelledby="linear-slider"
      />

      <Typography id="linear-slider" gutterBottom>
        Filter By Budget
      </Typography>
      <Slider
        name="age"
        value={age}
        min={0}
        step={1}
        max={75}
        getAriaValueText={valueLabelFormat}
        valueLabelFormat={valueLabelFormat}
        onChange={handleBudget}
        valueLabelDisplay="auto"
        aria-labelledby="linear-slider"
      />
    </div>
  );
};

export default Filter;
