import React from "react";
import { connect } from "react-redux";
import TextField from "@material-ui/core/TextField";
import InputLabel from "@material-ui/core/InputLabel";
import MenuItem from "@material-ui/core/MenuItem";
import ImageUploader from "./ImageUploader";

class EditProfile extends React.Component {
  state = {
    // name: this.props.currentUser.info.name,
    // summary: this.props.currentUser.info.name,
    image: null,
  };

  cleaning = [
    { value: "Every Few Days" },
    { value: "Once a Week" },
    { value: "Every Few Weeks" },
    { value: "Once a Month" },
  ];

  guests = [
    { value: "Fine With Having Guests" },
    { value: "Not Fine with Guests" }
  ]

  schedule = [
    { value: 'I Sleep Early' },
    { value: 'I Sleep Late' },
    { value: 'I Work Late' },

  ]

  clickHandler = (event) => {
    let formdata = new FormData();
    formdata.append("image", this.state.image);

    fetch(
      `http://localhost:3000/user/${this.props.currentUser.info.id}/upload_img`,
      {
        method: "POST",
        body: formdata,
      }
    );
  };

  selectImage = (image) => {
    this.setState({ image });
  };

  render() {
    return !this.props.currentUser ? (
      <div>Loading ...</div>
    ) : (
      <div className="user-edit-form">
        <form>
          <ImageUploader selectImage={this.selectImage} />

          <div className="edit-user-info">
            <h1> Edit User Information </h1>
            <div className="edit-user-name-work">
              <div className="edit-user-name">
                <InputLabel>Name</InputLabel>
                <TextField
                  id="outlined-name"
                  // value={name}
                  // onChange={handleChange}
                  variant="outlined"
                />
              </div>

              <div className="edit-user-name">
                <InputLabel>Work Info</InputLabel>
                <TextField
                  id="outlined-work-info"
                  // value={name}
                  // onChange={handleChange}
                  variant="outlined"
                />
              </div>
            </div>

            <div className="edit-user-summary">
              <InputLabel>Summary</InputLabel>
              <TextField
                id="outlined-multiline-static"
                multiline
                rows={5}
                // defaultValue="Default Value"
                variant="outlined"
              />
            </div>
          </div>

          <div className="edit-user-roommate-preferences">
            <h1> Edit Roommate Preferences </h1>
            <InputLabel>How Often Do You Clean?</InputLabel>
            <TextField
              id="outlined-select-currency"
              select
              // value={currency}
              // onChange={handleChange}
              variant="outlined"
            >
              {this.cleaning.map((option) => (
                <MenuItem key={option.value} value={option.value}>
                  {option.value}
                </MenuItem>
              ))}
            </TextField>

            <InputLabel>How Do You Feel About Guests?</InputLabel>
            <TextField
              id="outlined-select-currency"
              select
              // value={currency}
              // onChange={handleChange}
              variant="outlined"
            >
              {this.cleaning.map((option) => (
                <MenuItem key={option.value} value={option.value}>
                  {option.value}
                </MenuItem>
              ))}
            </TextField>
          </div>

          <TextField
            id="outlined-budget"
            label="Budget"
            type="number"
            InputLabelProps={{
              shrink: true,
            }}
            variant="outlined"
          />

          <button onClick={this.clickHandler}>Submit</button>
        </form>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return { currentUser: state.auth.currentUser };
};

export default connect(mapStateToProps)(EditProfile);
