import React from "react";
import UserCard from "./UserCard";
import { fetchUsers } from "../actions";
import { connect } from "react-redux";

class FindRoomate extends React.Component {
  state = {
    filteredUsers: [],
  };

  componentDidMount() {
    this.props.fetchUsers();
  }

  componentDidUpdate() {
      if(this.props.currentUser) {
          this.setState({
              filteredUsers: this.props.users.filter(user => user.id !== this.props.currentUser.info.id)
          })
      }
  }

  render() {

    return (
      // !this.props.currentUser ? <div>LOADING...</div> : (
      <div className="ui four stackable cards">
        {this.state.filteredUsers.map((user) => (
          <UserCard key={user.id} {...user} />
        ))}
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    users: state.users.users,
    currentUser: state.auth.currentUser,
  };
};

export default connect(mapStateToProps, { fetchUsers })(FindRoomate);
